&lt;div class=&quot;row&quot;&gt;
  &lt;div class=&quot;col&quot;&gt;display text goes here&lt;/div&gt;
&lt;/div&gt;
&lt;div class=&quot;row&quot;&gt;
  &lt;div class=&quot;col&quot;&gt;btn&lt;/div&gt;
  &lt;div class=&quot;col&quot;&gt;btn&lt;/div&gt;
  &lt;div class=&quot;col&quot;&gt;btn&lt;/div&gt;
  &lt;div class=&quot;col&quot;&gt;btn&lt;/div&gt;
&lt;/div&gt;
&lt;div class=&quot;row&quot;&gt;
  &lt;div class=&quot;col&quot;&gt;btn&lt;/div&gt;
  &lt;div class=&quot;col&quot;&gt;btn&lt;/div&gt;
  &lt;div class=&quot;col&quot;&gt;btn&lt;/div&gt;
  &lt;div class=&quot;col&quot;&gt;btn&lt;/div&gt;
&lt;/div&gt;
&lt;div class=&quot;row&quot;&gt;
  &lt;div class=&quot;col&quot;&gt;btn&lt;/div&gt;
  &lt;div class=&quot;col&quot;&gt;btn&lt;/div&gt;
  &lt;div class=&quot;col&quot;&gt;btn&lt;/div&gt;
